				echo "<b>Check <a href='".SITE_URL."installation-instruction.php'>installation instructions</a> to resolve this problem.</b>";
				exit;
			}else{
				$conn = new mysqli(SAASAPPOINT_HOSTNAME, SAASAPPOINT_USERNAME, SAASAPPOINT_PASSWORD, SAASAPPOINT_DATABASE);
				/* Check connection */
				if ($conn->connect_error) {
					@ob_clean();
					ob_start();
					/** redirect to installation page **/
					echo $conn->connect_error;
					echo "<br /><br /><br />";
					echo "<b>Check <a href='".SITE_URL."installation-instruction.php'>installation instructions</a> to resolve this problem.</b>";
					exit;
				}else{
					/* Select 1 from tables will return false if the table does not exist. */
					$val = mysqli_query($conn, 'SELECT 1 FROM `saasappoint_used_fd_by_customer`, `saasappoint_used_coupons_by_customer`, `saasappoint_support_tickets`, `saasappoint_support_ticket_discussions`, `saasappoint_superadmins`, `saasappoint_superadmin_settings`, `saasappoint_subscriptions_history`, `saasappoint_subscriptions`, `saasappoint_subscription_plans`, `saasappoint_settings`, `saasappoint_services`, `saasappoint_schedule`, `saasappoint_payments`, `saasappoint_frequently_discount`, `saasappoint_feedback`, `saasappoint_customers`, `saasappoint_customer_orderinfo`, `saasappoint_coupons`, `saasappoint_categories`, `saasappoint_businesses`, `saasappoint_business_type`, `saasappoint_bookings`, `saasappoint_admins`, `saasappoint_addons` limit 1');
					if($val === FALSE){
						echo "<b>Some MYSQL tables are missing please import provided SQL file properly. (SQL file located at database folder)</b>";
						exit;
					}else{
						return $conn;
					}
				}
			}
		}else{
			header("location:".SITE_URL."installation-instruction.php");exit;
		}
    }
	
