	/** Version Updater START **/
	/* Function for update version option value */
	public function saasappoint_update_version_option($version,$conn){
		$res = mysqli_query($conn, "select `option_value` from `".$this->saasappoint_superadmin_settings."` where `option_name` = 'saasappoint_version'");
		if($res){
			if(mysqli_num_rows($res)>0){
				$result = mysqli_query($conn, "update `".$this->saasappoint_superadmin_settings."` set `option_value`='".$version."' where `option_name` = 'saasappoint_version'");
			}else{
				$result = mysqli_query($conn, "insert into `".$this->saasappoint_superadmin_settings."` (`id`, `option_name`, `option_value`) VALUES (NULL, 'saasappoint_version', '".$version."')");
			}
		}else{
			$result=mysqli_query($conn, "insert into `".$this->saasappoint_superadmin_settings."` (`id`, `option_name`, `option_value`) VALUES (NULL, 'saasappoint_version', '".$version."')");
		}
		return $result;
    }
	
	/* Function for get version option value */
	public function saasappoint_get_version_option($conn){
		$query = "select `option_value` from `".$this->saasappoint_superadmin_settings."` where `option_name` = 'saasappoint_version'";
		$result=mysqli_query($conn,$query);
		if(mysqli_num_rows($result)>0){
			$settings_data = mysqli_fetch_array($result);
			return $settings_data["option_value"];
		}else{
			return "1.0";
		}
    }
	
	/* Function for version update */
	public function saasappoint_version_update($conn){
		$current_version = $this->saasappoint_get_version_option($conn);
		if($current_version<2.0){
			
			/** Alter queries below **/
			mysqli_query($conn, "ALTER TABLE `saasappoint_superadmin_settings` CHANGE `option_value` `option_value` LONGTEXT NOT NULL;");
			mysqli_query($conn, "ALTER TABLE `saasappoint_settings` CHANGE `option_value` `option_value` LONGTEXT NOT NULL;");
			mysqli_query($conn, "ALTER TABLE `saasappoint_subscriptions` ADD `payment_method` VARCHAR(255) NOT NULL;");
