						  `payment_method` varchar(255) NOT NULL,
						  `extended_on` datetime NOT NULL,
						  PRIMARY KEY (`id`)
						) ENGINE=InnoDB DEFAULT CHARSET=latin1;");
			mysqli_query($conn, "CREATE TABLE IF NOT EXISTS `saasappoint_templates` (
						  `id` int(11) NOT NULL AUTO_INCREMENT,
						  `business_id` int(11) NOT NULL,
						  `template` varchar(255) NOT NULL,
						  `subject` varchar(500) NOT NULL,
						  `email_content` longtext NOT NULL,
						  `sms_content` longtext NOT NULL,
						  `template_for` varchar(255) NOT NULL,
						  `email_status` enum('Y','N') NOT NULL,
						  `sms_status` enum('Y','N') NOT NULL,
						  PRIMARY KEY (`id`)
						) ENGINE=InnoDB DEFAULT CHARSET=latin1;");
			mysqli_query($conn, "CREATE TABLE IF NOT EXISTS `saasappoint_customer_referrals` (
						  `id` int(11) NOT NULL AUTO_INCREMENT,
						  `order_id` int(11) NOT NULL,
