	public function insert_admin_setting_options($conn, $settings_array){
		$businesses = mysqli_query($conn, "select `id` from `saasappoint_businesses`");
		if(mysqli_num_rows($businesses)>0){
			while($business = mysqli_fetch_array($businesses)){
				foreach($settings_array as $key => $value){
					/** Insert into table queries below **/
					$result=mysqli_query($conn, "select `option_value` from `saasappoint_settings` where `option_name` = '".$key."' and `business_id` = '".$business["id"]."'");
					if(mysqli_num_rows($result)==0){
						if($key == "saasappoint_location_selector"){
							$res1=mysqli_query($conn, "select `option_value` from `saasappoint_settings` where `option_name` = 'saasappoint_company_zip' and `business_id` = '".$business["id"]."'");
							if(mysqli_num_rows($res1)>0){
								$val1 = mysqli_fetch_array($res1);
								$value = $val1["option_value"];
							}
						}
						mysqli_query($conn, "insert into `saasappoint_settings` (`id`, `business_id`, `option_name`, `option_value`) VALUES (NULL, '".$business["id"]."', '".$key."', '".$value."')");
					}
				}
			}
		}
	}
	
	public function insert_subscription_payment_method_and_referral_code($conn){
		mysqli_query($conn, "UPDATE `saasappoint_subscriptions` SET `payment_method`='stripe'");
		mysqli_query($conn, "UPDATE `saasappoint_subscriptions_history` SET `payment_method`='stripe'");
		$customers = mysqli_query($conn, "select `id` from `saasappoint_customers`");
		if(mysqli_num_rows($customers)>0){
			while($customer = mysqli_fetch_array($customers)){
				$chars = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ";
				$refferral_code = "";
				for ($i = 0; $i < 15; $i++) {
					$refferral_code .= $chars[mt_rand(0, strlen($chars)-1)];
				}
				mysqli_query($conn, "UPDATE `saasappoint_customers` SET `refferral_code`='".$refferral_code."' where `refferral_code` = '".$customer["id"]."'");
			}
		}
	}
	
	public function insert_admin_templates_setting($conn){
		$businesses = mysqli_query($conn, "select `id` from `saasappoint_businesses`");
		if(mysqli_num_rows($businesses)>0){
			while($business = mysqli_fetch_array($businesses)){
				/** Add default Email SMS templates queries **/
				$this->add_default_email_sms_templates($business["id"], $conn);
			}
		}
	}
	
	public function insert_superadmin_setting_options($conn, $settings_array){
		foreach($settings_array as $key => $value){
			/** Insert into table queries below **/
			$result=mysqli_query($conn, "select `option_value` from `saasappoint_superadmin_settings` where `option_name` = '".$key."'");
			if(mysqli_num_rows($result)==0){
				mysqli_query($conn, "insert into `saasappoint_superadmin_settings` (`id`, `option_name`, `option_value`) VALUES (NULL, '".$key."', '".$value."')");
			}
		}
	}
	
