	/* Function to read one template */
	public function readone_template(){
		$query = "select * from `".$this->saasappoint_templates."` where `template`='".$this->template."' and `template_for`='".$this->template_for."' and `business_id`='".$this->business_id."'";
		$result=mysqli_query($this->conn,$query);
		return $result;
	}
	
	/* Function to update email template */
	public function update_email_template(){
		$query = "update `".$this->saasappoint_templates."` set `subject` = '".$this->subject."', `email_content` = '".$this->email_content."', `email_status` = '".$this->email_status."' where `template` = '".$this->template."' and `template_for` = '".$this->template_for."' and `business_id` = '".$this->business_id."'";
		$result=mysqli_query($this->conn,$query);
		return $result;
	}
	
	/* Function to update SMS template */
	public function update_sms_template(){
		$query = "update `".$this->saasappoint_templates."` set `sms_content` = '".$this->sms_content."', `sms_status` = '".$this->sms_status."' where `template` = '".$this->template."' and `template_for` = '".$this->template_for."' and `business_id` = '".$this->business_id."'";
		$result=mysqli_query($this->conn,$query);
		return $result;
	}
	
